import React, { useState, useEffect } from "react";

const App = () => {
  const [value, setValue] = useState("");
  const [message, setMessage] = useState(null);
  const [previousChats, setPreviousChats] = useState([]);
  const [currentTitle, setCurrentTitle] = useState(null);
  const [logoImageLoaded, setLogoImageLoaded] = useState(false);

  const createNewChat = () => {
    setMessage(null);
    setValue("");
    setCurrentTitle(null);
  };

  const handleClick = (uniqueTitle) => {
    setCurrentTitle(uniqueTitle);
    setMessage(null);
    setValue("");
  };

  const getMessages = async () => {
    const options = {
      method: "POST",
      body: JSON.stringify({
        message: value,
      }),
      headers: { "Content-Type": "application/json" },
    };

    try {
      const response = await fetch(
        "http://localhost:8000/completions",
        options
      );
      const data = await response.json();

      setMessage(data.choices[0].message);
    } catch (error) {
      console.error(error);
    }
  };

  useEffect(() => {
    if (!currentTitle && value && message) {
      setCurrentTitle(value);
    }

    if (currentTitle && value && message) {
      setPreviousChats((prevChats) => [
        ...prevChats,
        {
          title: currentTitle,
          role: "user",
          content: value,
        },
        {
          title: currentTitle,
          role:
            message.role === "ncllexGPT" ? (
              <img
                src={process.env.PUBLIC_URL + "/logo192.png"}
                alt="Ncllex Logo"
                style={{ width: "50px", height: "50px" }}
                onLoad={() => setLogoImageLoaded(true)}
                onError={() => console.error("Error loading logo image")}
              />
            ) : (
              message.role
            ),
          content: message.content,
        },
      ]);
    }
  }, [currentTitle, value, message]);

  const currentChat = previousChats.filter(
    (chatMessage) => chatMessage.title === currentTitle
  );

  const uniqueTitles = Array.from(
    new Set(previousChats.map((chatMessage) => chatMessage.title))
  );

  return (
    <div>
      <div className="app">
        <section className="side-bar">
          <button onClick={createNewChat}>+ New chat </button>
          <ul className="history">
            {uniqueTitles?.map((uniqueTitle, index) => (
              <li key={index} onClick={() => handleClick(uniqueTitle)}>
                {uniqueTitle}
              </li>
            ))}
          </ul>
          <nav>
            <p>Made by Benasco</p>
          </nav>
        </section>
        <section className="main">
          {!currentTitle && <h1>NcllexGPT</h1>}
          <ul className="feed">
            {currentChat?.map((chatMessage, index) => (
              <li key={index}>
                <p className="role">{chatMessage.role}</p>
                <p>{chatMessage.content}</p>
              </li>
            ))}
          </ul>
          <div className="bottom-section">
            <div className="input-container">
              <input value={value} onChange={(e) => setValue(e.target.value)} />
              <div id="submit" onClick={getMessages}>
                ➢
              </div>
            </div>
            <p className="info">
              ⚠️ Disclaimer: NclexGPT's responses are for educational purposes
              only. Not intended as medical advice. Consult healthcare
              professionals for accurate guidance.
            </p>
          </div>
        </section>
      </div>
    </div>
  );
};

export default App;
